package sliding.window.technique;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;
import java.util.stream.Stream;

class LongestPalindromicSubstringTest {

    @ParameterizedTest
    @MethodSource("provideInputStringAndExpectedResult")
    void find(String inputStr, String expectedResult) {
        LongestPalindromicSubstring longestPalindromicSubstring = new LongestPalindromicSubstring();
        Assertions.assertEquals(expectedResult, longestPalindromicSubstring.find(inputStr));
    }



    private static Stream<Arguments> provideInputStringAndExpectedResult() {
        return Stream.of(
                Arguments.of("DDDD", "DDDD"),
                Arguments.of("BABAD", "BAB"),
                Arguments.of("CBBD", "BB"),
                Arguments.of("AAA", "AAA"),
                Arguments.of("PAOLO", "OLO"),
                Arguments.of("ALESSANDRA", "SS"),
                Arguments.of("", "fklkf"));
    }
}